// Добавляем оставшиеся главы (с 35 по 75) как пустышки для заполнения
for (let i = 44; i <= 75; i++) {
    chaptersData.push({
        id: i,
        text: ["[Текст этой главы еще не написан...]"]
    });
}

// --- ЛОГИКА РЕНДЕРИНГА ---
document.addEventListener('DOMContentLoaded', () => {
    const contentArea = document.getElementById('content-area');
    const navAccordion = document.getElementById('nav-accordion');
    const scrollTopBtn = document.getElementById('scrollTopBtn');

    // Группируем главы по томам
    const chaptersByVolume = {};
    for (const [volId, volData] of Object.entries(volumes)) {
        chaptersByVolume[volId] = [];
    }

    // Рендер контента
    chaptersData.forEach(chapter => {
        const chNum = chapter.id;
        
        // Пропускаем 7 главу, так как она объединена
        if (chNum === 7) return;

        // Определяем том
        let currentVolId = null;
        for (const [volId, range] of Object.entries(volumes)) {
            if (chNum >= range.start && chNum <= range.end) {
                currentVolId = volId;
                break;
            }
        }

        if (currentVolId) {
            chaptersByVolume[currentVolId].push(chNum);
        }

        // Рендер обложки тома (если это первая глава тома)
        const volInfo = volumes[currentVolId];
        if (volInfo && chNum === volInfo.start) {
            const volHtml = `
                <div class="volume-section" id="vol-${currentVolId}">
                    <img src="${volInfo.img}" alt="Volume ${currentVolId}" class="volume-cover">
                    <div class="volume-title">Том ${currentVolId}</div>
                </div>
            `;
            contentArea.insertAdjacentHTML('beforeend', volHtml);
        }

        // Рендер главы
        const title = chapterTitles[chNum] || `Глава ${chNum}`;
        const formattedText = chapter.text.map(p => {
            if (p.trim().startsWith('[') || p.includes(': [')) {
                return `<div class="quote">${p}</div>`;
            }
            return `<p>${p}</p>`;
        }).join('');

        const chapterHtml = `
            <article class="chapter-card" id="ch-${chNum}">
                <div class="chapter-header">
                    <div class="chapter-number">Глава ${chNum}</div>
                    <div class="chapter-title">${title}</div>
                </div>
                <div class="chapter-text">
                    ${formattedText}
                </div>
            </article>
        `;
        contentArea.insertAdjacentHTML('beforeend', chapterHtml);
    });

    // --- ГЕНЕРАЦИЯ МЕНЮ (АККОРДЕОН) ---
    for (const [volId, chList] of Object.entries(chaptersByVolume)) {
        if (chList.length === 0) continue;

        const volGroup = document.createElement('div');
        volGroup.className = 'volume-group';

        const volBtn = document.createElement('button');
        volBtn.className = 'volume-btn';
        volBtn.innerHTML = `Том ${volId} <i class='bx bx-chevron-down icon'></i>`;
        
        const listUl = document.createElement('ul');
        listUl.className = 'chapter-list';

        chList.forEach(chNum => {
            const li = document.createElement('li');
            const chTitleShort = chapterTitles[chNum] ? chapterTitles[chNum].split(' ').slice(0, 4).join(' ') + '...' : `Глава ${chNum}`;
            li.innerHTML = `<a href="#ch-${chNum}" data-target="ch-${chNum}">${chNum}. ${chTitleShort}</a>`;
            listUl.appendChild(li);
        });

        // Клик по кнопке тома
        volBtn.addEventListener('click', () => {
            volBtn.classList.toggle('active');
            listUl.classList.toggle('show');
        });

        volGroup.appendChild(volBtn);
        volGroup.appendChild(listUl);
        navAccordion.appendChild(volGroup);
    }

    // --- ПЛАВНАЯ ПРОКРУТКА И ПОДСВЕТКА ---
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();
            const targetId = this.getAttribute('href').substring(1);
            const target = document.getElementById(targetId);
            
            if(target) {
                // Убираем подсветку у других
                document.querySelectorAll('.chapter-list a').forEach(a => a.classList.remove('current'));
                this.classList.add('current');

                // Скролл с отступом
                const offset = 20; 
                const elementPosition = target.getBoundingClientRect().top;
                const offsetPosition = elementPosition + window.pageYOffset - offset;
    
                window.scrollTo({
                    top: offsetPosition,
                    behavior: "smooth"
                });
            }
        });
    });

    // Кнопка наверх
    window.onscroll = function() {
        if (document.body.scrollTop > 500 || document.documentElement.scrollTop > 500) {
            scrollTopBtn.style.display = "block";
        } else {
            scrollTopBtn.style.display = "none";
        }
    };
    scrollTopBtn.addEventListener('click', () => {
        window.scrollTo({top: 0, behavior: 'smooth'});
    });
});